INSERT INTO `theme_modules_fixed` (`modul_id`, `modul_status`, `modul_file`, `modul_title`, `modul_description`) VALUES
(1, 1, 'poprzednio_ogladane.php', 'Ostatnio oglądane produkty', 'Statyczne wyświetlanie ostatnio oglądanych produktów - lista na stałe widoczna na dole ekranu.'),
(2, 1, 'informacja_o_cookie.php', 'Informacja o ciasteczkach (cookie)', 'Wyświetla informację, że sklep korzysta z ciasteczek (cookies). Informacja jest wyświetlana na dole strony. Treść wyświetlanej informacji można zmienić w menu Konfiguracja / Tłumaczenia - sekcje MODULY_STALE'),
(3, 0, 'blokada_18_plus.php', 'Blokada 18+', 'Moduł Blokada 18+ powoduje, że po wejściu na stronę wyświetla się komunikat z informacją, że strona przeznaczona jest dla osób dorosłych i aby wejść na stronę należy potwierdzić że jest się osobą pełnoletnią. Treść wyświetlanej informacji można zmienić w menu Konfiguracja / Tłumaczenia - sekcje MODULY_STALE'),
(4, 0, 'informacja_tekstowa.php', 'Informacja na górze sklepu', 'Wyświetla informację o aktualizacji sklepu. Informacja jest wyświetlana na górze strony i nie można jej wyłączyć. Treść wyświetlanej informacji można zmienić w menu Konfiguracja / Tłumaczenia - sekcje MODULY_STALE'),
(5, 0, 'newsletter_popup.php', 'Newsletter popup', 'Wyświetla okno z możliwością zapisania do newslettera. Za zapisanie do newslettera jest generowany kupon rabatowy, który jest przesyłany do klienta na maila');
